Optional[{
    Frame msg;
    if (!errori()) {
        Soggiornante sogg = readDatiSogg(prenotazione, num_sogg + 1);
        sogg.setInizioSogg(new Date());
        sogg.setFineSogg(prenotazione.getFineSogg());
        sogg.setNumStanza(stanza.getNumStanza());
        int j;
        if ((j = (Principale.db).writeSoggiornante(sogg)) == DataBase.OK) {
            //	Modifico la prenotazione relativa
            if (stanza.getPostiLetto() > num_sogg)
                prenotazione.setNumPers(num_sogg + 1);
            else if ((stanza.getPostiLetto() == num_sogg) && (stanza.getDispLettoAgg() == Const.SI))
                prenotazione.setRichLettoAgg(Const.SI);
            (Principale.db).changePrenotazione(prenotazione.getIdPrenotazione(), prenotazione.getNumStanza(), prenotazione.getNumPers(), prenotazione.getNome(), prenotazione.getCognome(), prenotazione.getNumTel(), prenotazione.getInizioSogg(), prenotazione.getFineSogg(), prenotazione.getDataPren(), prenotazione.getCaparra(), prenotazione.getRichLettoAgg(), prenotazione.getPensionamento(), prenotazione.getTramiteAgenzia(), prenotazione.getNomeAgenzia(), prenotazione.getRichParticolari());
            msg = new AvvisoLocale(this, " Nuovo soggiornante registrato! ");
        } else
            msg = new MessageDialog(this, "Problemi con il database: " + DataBase.strErrore(j));
    }
}]

