Optional[{
    boolean toReturn = false;
    if (pBanner != null) {
        toReturn = (pBanner.getId() > 0 && !pBanner.getPercorsoFile().isEmpty() && pBanner.getIdPuntoDiRistoro() > 0);
    }
    return toReturn;
}]

Optional[{
    boolean toReturn = false;
    /* Check the validity of the general method parameter */
    if (pNews != null) {
        // Release Date
        Date dataPubb = pNews.getDataPubblicazione();
        // Due Date
        Date dataScad = pNews.getDataScadenza();
        // Text of News
        String news = pNews.getNews();
        /* Checking the validity of the fields */
        if (dataPubb != null && dataScad != null && news != null) {
            /* Check the consistency of the dates */
            toReturn = dataPubb.before(dataScad);
            /* Check that the text is not empty */
            toReturn = toReturn && !news.isEmpty();
            /* Check that the ID is greater than 0 */
            toReturn = toReturn && (pNews.getId() > 0);
            /* Check the priority value */
        }
    }
    return toReturn;
}]
