Optional[{
    try {
        long mid = Long.valueOf(input);
        PersonnelBean personnel = personnelDAO.getPersonnel(mid);
        if (personnel != null) {
            transDAO.logTransaction(TransactionType.ENTER_EDIT_DEMOGRAPHICS, loggedInMID, mid, //$NON-NLS-1$
            Messages.getString("ViewPersonnelAction.0") + mid);
            return personnel;
        } else
            //$NON-NLS-1$
            throw new iTrustException(Messages.getString("ViewPersonnelAction.1"));
    } catch (NumberFormatException e) {
        e.printStackTrace();
        //$NON-NLS-1$
        throw new iTrustException(Messages.getString("ViewPersonnelAction.2"));
    }
}]

