public News() {
        super("News");
        setPreferredSize(Home.CHILD_SIZE);
        setMinimumSize(new Dimension(600, 480));
        setResizable(true);
        setFrameIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "News32.png")));
        setIconifiable(true);
        setMaximizable(true);
        setClosable(true);

        // Setting up the help manager to manage the news.
        textGuida = new JTextPane();
        try {
            newsHelp = new HelpManager(Home.URL_HELP + "news.txt", textGuida);
        } catch (FileNotFoundException e) {
            textGuida.setText("<html> <b> Help not available </b> </html>");
        }

        setContentPane(getJContentPane());

        addInternalFrameListener(new InternalFrameAdapter() {
            public void internalFrameOpened(InternalFrameEvent pEvent) {
                JInternalFrame frame = pEvent.getInternalFrame();
                jDesktopPane = frame.getDesktopPane();
                desktopManager = (DeskManager) jDesktopPane.getDesktopManager();

                // Setting up objects for remote asset management
                try {
                    Registry reg = LocateRegistry.getRegistry(Home.HOST);
                    gestioneNews = (IGestioneAdvertisementAgenzia) reg.lookup("GestioneAdvertisementAgenzia");

                    // Load data.
                    caricaTabella();
                } catch (Exception ex) {
                    JLabel error = new JLabel(
                            "<html> <h2> Unable to communicate with the server eTour. </h2>"
                                    + "<h3> <u> The dialog management request is closed. </u> </h3>"
                                    + "<p> <b> Possible Causes: </b>"
                                    + "<ul> <li> No connection to the network. </li>"
                                    + "<li> Server inactive. </li>"
                                    + "<li> Server clogged. </li> </ul>"
                                    + "<p> Please try again later. </p>"
                                    + "<p> If the error persists, please contact technical support. </p>"
                                    + "<p> We apologize for the inconvenience. </html>");
                    Icon err = new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error48.png"));
                    JOptionPane.showMessageDialog(jDesktopPane, error, "Error!", JOptionPane.ERROR_MESSAGE, err);
                    frame.dispose();
                }
            }
        });
    }

private JPanel getJContentPane() {
        if (null == jContentPane) {
            jContentPane = new JPanel();
            jContentPane.setLayout(new BorderLayout());
            jContentPane.add(getNewsToolbar(), BorderLayout.NORTH);
            jContentPane.add(getRightPanel(), BorderLayout.EAST);
            jContentPane.add(getTableNews(), BorderLayout.CENTER);
        }
        return jContentPane;
    }

private JToolBar getNewsToolbar() {
        if (null == newsToolbar) {
            newsToolbar = new JToolBar();
            newsToolbar.setFloatable(false);
            newsToolbar.add(getBtnModificaN());
            newsToolbar.addSeparator();
            newsToolbar.add(getBtnEliminaN());
        }
        return newsToolbar;
    }

private JButton getBtnModificaN() {
        if (null == btnModificaN) {
            btnModificaN = new JButton();
            btnModificaN.setText("Edit News");
            btnModificaN.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "ModificaNews32.png")));
            btnModificaN.setCursor(Cursor.getPredefinedCursor(Cursor.HAND_CURSOR));
            btnModificaN.setName("btnModifica");
            btnModificaN.addMouseListener(newsHelp);
            btnModificaN.setEnabled(false);
            btnModificaN.addActionListener(new ActionListener() {
                public void actionPerformed(ActionEvent pActionEvent) {
                    int selectedRow = tableNews.getSelectedRow();
                    if (idNews == -1) { // In this way I know if she was edited
                        btnInsertModify.setText("Change");
                        btnInsertModify.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "Salva16.png")));
                        btnReset.setText("Cancel");
                        btnReset.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "Annulla16.png")));
                        formNews.setBorder(BorderFactory.createTitledBorder(BorderFactory.createLineBorder(new Color(51, 102, 255), 3), "Edit News", TitledBorder.DEFAULT_JUSTIFICATION, TitledBorder.DEFAULT_POSITION, new Font("Dialog", Font.BOLD, 12), new Color(0, 102, 204)));
                    }
                    String text = (String) tableModel.getValueAt(selectedRow, 0);
                    int numCaratteri = 200 - text.length() + 1;
                    labelCaratteri.setText("# Characters: " + numCaratteri);
                    Document docTesto = testoNews.getDocument();
                    try {
                        docTesto.remove(0, docTesto.getLength());
                        docTesto.insertString(0, text, null);
                    } catch (BadLocationException s) {
                        s.printStackTrace();
                    }
                    Date expires = (Date) tableModel.getValueAt(selectedRow, 3);
                    durataNews.setSelectedIndex(Data.getNumDays(expires));
                    idNews = tableModel.getID(selectedRow);
                    prSlider.setValue((Integer) tableModel.getValueAt(selectedRow, 1));
                }
            });
        }
        return btnModificaN;
    }

private JButton getBtnEliminaN() {
        if (null == btnEliminaN) {
            btnEliminaN = new JButton();
            btnEliminaN.setText("Delete News");
            btnEliminaN.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "EliminaNews32.png")));
            btnEliminaN.setEnabled(false);
            btnEliminaN.setCursor(Cursor.getPredefinedCursor(Cursor.HAND_CURSOR));
            btnEliminaN.setName("btnElimina");
            btnEliminaN.addMouseListener(newsHelp);
            btnEliminaN.addActionListener(new ActionListener() {
                public void actionPerformed(ActionEvent pEvent) {
                    int selectedRow = tableNews.getSelectedRow();

                    // Construction of the dialog for confirmation of deletion
                    JPanel root = new JPanel(new BorderLayout());
                    JLabel message = new JLabel("Are you sure you want to delete the selected news?");
                    message.setFont(new Font("Dialog", Font.BOLD, 14));
                    JLabel alert = new JLabel("The deleted data cannot be filled again.", SwingConstants.CENTER);
                    alert.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "warning16.png")));
                    root.add(message, BorderLayout.NORTH);
                    root.add(alert, BorderLayout.CENTER);
                    String[] options = {"Delete", "Cancel"};

                    // The dialog screen appears
                    int choice = JOptionPane.showInternalOptionDialog(jContentPane, root, "Confirm Delete News", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "EliminaNews48.png")), options, options[1]);

                    // If you chose to confirm the deletion
                    if (choice == JOptionPane.YES_OPTION) {
                        try {
                            gestioneNews.cancellaNews(tableModel.getID(selectedRow));
                            tableModel.removeNews(selectedRow);
                            JOptionPane.showInternalMessageDialog(jContentPane, "The news has been selected successfully deleted", "News out!", JOptionPane.OK_OPTION, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "Ok48.png")));
                            azzeraForm();
                        } catch (Exception ex) {
                            JLabel error = new JLabel(
                                    "<html> <h2> Unable to communicate with the server eTour. </h2>"
                                            + "<h3> <u> Delete operation request cannot be completed. </u> </h3>"
                                            + "<p> Please try again later. </p>"
                                            + "<p> If the error persists, please contact technical support. </p>"
                                            + "<p> We apologize for the inconvenience. </html>");
                            Icon err = new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error48.png"));
                            JOptionPane.showMessageDialog(jDesktopPane, error, "Error!", JOptionPane.ERROR_MESSAGE, err);
                        }
                    }
                }
            });
        }
        return btnEliminaN;
    }

private JPanel getRightPanel() {
        if (null == rightPanel) {
            rightPanel = new JPanel();
            rightPanel.setLayout(new GridBagLayout());
            GridBagConstraints g = new GridBagConstraints();
            g.fill = GridBagConstraints.BOTH;
            g.gridx = 0;
            g.gridy = 0;
            g.weighty = 0.7;
            rightPanel.add(getFormNews(), g);
            g.weighty = 0.3;
            g.gridy = 1;
            rightPanel.add(getPanelHelp(), g);
        }
        return rightPanel;
    }

private JPanel getFormNews() {
        if (null == formNews) {
            formNews = new JPanel(new GridBagLayout());
            formNews.setBorder(BorderFactory.createTitledBorder(BorderFactory.createLineBorder(new Color(51, 102, 255), 3), "New News", TitledBorder.DEFAULT_JUSTIFICATION, TitledBorder.DEFAULT_POSITION, new Font("Dialog", Font.BOLD, 12), new Color(0, 102, 204)));
            GridBagConstraints g = new GridBagConstraints();
            g.anchor = GridBagConstraints.CENTER;
            g.gridx = 0;
            g.gridy = 0;
            g.weighty = 0.1;
            g.gridwidth = 1;
            g.gridheight = 1;
            g.insets = new Insets(5, 5, 5, 5);
            formNews.add(new JLabel("Text of the news:"), g);
            labelCaratteri = new JLabel("# Characters: 200");
            g.gridx = 1;
            formNews.add(labelCaratteri, g);
            g.gridx = 0;
            g.gridwidth = 2;
            g.gridy = 1;
            g.weighty = 0.3;
            g.fill = GridBagConstraints.VERTICAL;
            JScrollPane scrollTesto = new JScrollPane(getTestoNews());
            scrollTesto.setVerticalScrollBarPolicy(JScrollPane.VERTICAL_SCROLLBAR_ALWAYS);
            formNews.add(scrollTesto, g);
            g.weighty = 0.1;
            g.gridy = 2;
            g.fill = GridBagConstraints.NONE;
            formNews.add(new JLabel("Priority:"), g);
            g.gridy = 3;
            formNews.add(getPrSlider(), g);
            JPanel nuovo = new JPanel(new FlowLayout());
            nuovo.add(new JLabel("Length of news:"));
            durataNews = new JComboBox<>();
            for (int i = 0; i <= 60; i++) {
                durataNews.addItem("" + i);
            }
            durataNews.setSelectedIndex(0);
            durataNews.setEditable(false);
            durataNews.setName("duration");
            durataNews.addMouseListener(newsHelp);
            nuovo.add(durataNews);
            nuovo.add(new JLabel("days"));
            g.gridy = 4;
            g.fill = GridBagConstraints.HORIZONTAL;
            formNews.add(nuovo, g);
            g.gridwidth = 1;
            g.gridy = 5;
            formNews.add(getBtnInsertModify(), g);
            g.gridx = 1;
            formNews.add(getBtnReset(), g);
        }
        return formNews;
    }

private JSlider getPrSlider() {
        if (null == prSlider) {
            prSlider = new JSlider(JSlider.HORIZONTAL, 1, 5);
            prSlider.setMinimum(1);
            prSlider.setMaximum(5);
            prSlider.setMajorTickSpacing(1);
            prSlider.setLabelTable(prSlider.createStandardLabels(1));
            prSlider.setCursor(Cursor.getPredefinedCursor(Cursor.HAND_CURSOR));
            prSlider.setName("priority");
            prSlider.addMouseListener(newsHelp);
            prSlider.setPaintLabels(true);
            prSlider.setPaintTicks(true);
            prSlider.setSnapToTicks(true);
        }
        return prSlider;
    }

private JButton getBtnInsertModify() {
        if (null == btnInsertModify) {
            btnInsertModify = new JButton();
            btnInsertModify.setText("Insert");
            btnInsertModify.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "nuovaNews16.png")));
            btnInsertModify.setCursor(Cursor.getPredefinedCursor(Cursor.HAND_CURSOR));
            btnInsertModify.setName("btnInserisci");
            btnInsertModify.addMouseListener(newsHelp);
            btnInsertModify.setFont(new Font("Dialog", Font.BOLD, 12));
            btnInsertModify.setHorizontalTextPosition(SwingConstants.TRAILING);
            btnInsertModify.addActionListener(new ActionListener() {
                public void actionPerformed(ActionEvent pEvent) {
                    if (testoNews.getText().length() == 0) {
                        JOptionPane.showInternalMessageDialog(jContentPane, "The text of a news cannot be empty!", "Error New News", JOptionPane.ERROR_MESSAGE, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error32.png")));
                        return;
                    }
                    if (idNews != -1) { // We're making a change
                        // Construction of the dialog for confirmation of edit
                        JPanel root = new JPanel(new BorderLayout());
                        JLabel message = new JLabel("Changing the selected news with new data?");
                        message.setFont(new Font("Dialog", Font.BOLD, 14));
                        JLabel alert = new JLabel("The previous data cannot be more recovered.", SwingConstants.CENTER);
                        alert.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "warning16.png")));
                        root.add(message, BorderLayout.NORTH);
                        root.add(alert, BorderLayout.CENTER);
                        String[] options = {"Edit", "Cancel"};
                        // The dialog screen appears
                        int choice = JOptionPane.showInternalOptionDialog(jContentPane, root, "Confirmation Change News", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "ModificaNews48.png")), options, options[1]);
                        // If you chose to confirm the change
                        if (choice == JOptionPane.YES_OPTION) {
                            try {
                                Date expires = new Date();
                                expires.setDate(expires.getDate() + durataNews.getSelectedIndex());
                                BeanNews newNews = new BeanNews(testoNews.getText(), new Date(), expires, prSlider.getValue(), idNews);
                                gestioneNews.modificaNews(newNews);
                                tableModel.updateNews(newNews);
                                JOptionPane.showInternalMessageDialog(jContentPane, "The news has been changed successfully.", "News changed!", JOptionPane.OK_OPTION, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "Ok48.png")));
                            } catch (Exception ex) {
                                JLabel error = new JLabel("<html> <h2> Unable to communicate with the server eTour. </h2>" + "<h3> <u> Change operation request cannot be completed. </u> </h3>" + "<p> Please try again later. </p>" + "<p> If the error persists, please contact technical support. </p>" + "<p> We apologize for the inconvenience. </html>");
                                Icon err = new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error48.png"));
                                JOptionPane.showMessageDialog(jDesktopPane, error, "Error!", JOptionPane.ERROR_MESSAGE, err);
                            }
                        }
                    } else { // We are posting
                        Date expires = new Date();
                        expires.setDate(expires.getDate() + durataNews.getSelectedIndex());
                        BeanNews newNews = new BeanNews(testoNews.getText(), new Date(), expires, prSlider.getValue(), 33);
                        try {
                            boolean ok = gestioneNews.inserisciNews(newNews);
                            if (ok) {
                                caricaTabella();
                                tableModel.insertNews(newNews);
                                JOptionPane.showInternalMessageDialog(jContentPane, "The news has been inserted correctly into the system.", "New news!", JOptionPane.OK_OPTION, new ImageIcon(getClass().getResource(Home.URL_IMAGES + "NuovaNews48.png")));
                            }
                        } catch (RemoteException e) {
                            JLabel error = new JLabel("<html> <h2> Unable to communicate with the server eTour. </h2>" + "<h3> <u> Insertion operation request cannot be completed. </u> </h3>" + "<p> Please try again later. </p>" + "<p> If the error persists, please contact technical support. </p>" + "<p> We apologize for the inconvenience. </html>");
                            Icon err = new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error48.png"));
                            JOptionPane.showMessageDialog(jDesktopPane, error, "Error!", JOptionPane.ERROR_MESSAGE, err);
                        }
                        tableModel.fireTableDataChanged();
                        azzeraForm();
                    }
                }
            });
        }
        return btnInsertModify;
    }

