private JScrollPane getTableNews() {
        if (null == tableNews) {
            tableModel = new NewsTableModel();
            tableNews = new ScrollableTable(tableModel);
            tableNews.setRowHeight(64);
            tableNews.setSelectionMode(ListSelectionModel.SINGLE_SELECTION);
            tableNews.setSelectionBackground(new Color(0xe6, 0xe6, 0xFA));
            tableNews.setColumnSelectionAllowed(false);
            ListSelectionModel selectionModel = tableNews.getSelectionModel();
            selectionModel.addListSelectionListener(new ListSelectionListener() {
                public void valueChanged(ListSelectionEvent event) {
                    int selectedRow = tableNews.getSelectedRow();
                    btnModificaN.setEnabled((selectedRow != -1));
                    btnEliminaN.setEnabled((selectedRow != -1));
                }
            });
            tableNews.addKeyListener(new KeyAdapter() {
                public void keyPressed(KeyEvent pKey) {
                    int keyCode = pKey.getKeyCode();
                    if (keyCode == KeyEvent.VK_ENTER) {
                        btnModificaN.doClick();
                    } else if ((keyCode == KeyEvent.VK_CANCEL) || (keyCode == KeyEvent.VK_BACK_SPACE)) {
                        btnEliminaN.doClick();
                    }
                }
            });

            scrollTableNews = new JScrollPane();
            scrollTableNews.setViewportView(tableNews);
            scrollTableNews.setVerticalScrollBarPolicy(JScrollPane.VERTICAL_SCROLLBAR_ALWAYS);
        }
        return scrollTableNews;
    }

private JTextArea getTestoNews() {
        if (null == testoNews) {
            testoNews = new JTextArea();
            testoNews.setWrapStyleWord(true);
            testoNews.setLineWrap(true);
            testoNews.setBorder(BorderFactory.createLoweredBevelBorder());
            testoNews.setColumns(18);
            testoNews.setLineWrap(true);
            testoNews.setRows(4);
            testoNews.setDocument(new LimitedDocument(200));
            testoNews.setName("text");
            testoNews.addMouseListener(newsHelp);
            testoNews.addKeyListener(new KeyAdapter() {
                public void keyTyped(KeyEvent pKeyEvent) {
                    char keyChar = pKeyEvent.getKeyChar();
                    if (Character.isDigit(keyChar) || Character.isLetter(keyChar) || Character.isWhitespace(keyChar)) {
                        int len = testoNews.getText().length();
                        if (len != 200) {
                            labelCaratteri.setText("# Characters: " + (200 - (testoNews.getText().length() + 1)));
                        }
                    }
                }

                public void keyPressed(KeyEvent pKeyEvent) {
                    int keyCode = pKeyEvent.getKeyCode();
                    if (keyCode == KeyEvent.VK_CANCEL || keyCode == KeyEvent.VK_BACK_SPACE) {
                        int len = testoNews.getText().length();
                        if (len != 0) {
                            labelCaratteri.setText("# Characters: " + (200 - len + 1));
                        }
                    }
                }
            });
        }
        return testoNews;
    }

private void azzeraForm() {
        btnInsertModify.setText("Insert");
        btnReset.setText("Clear");
        btnReset.setIcon(new ImageIcon(getClass().getResource(Home.URL_IMAGES + "azzera16.png")));
        formNews.setBorder(BorderFactory.createTitledBorder(BorderFactory.createLineBorder(new Color(51, 102, 255), 3), "New News", TitledBorder.DEFAULT_JUSTIFICATION, TitledBorder.DEFAULT_POSITION, new Font("Dialog", Font.BOLD, 12), new Color(0, 102, 204)));
        durataNews.setSelectedIndex(0);
        testoNews.setText("");
        tableNews.clearSelection();
        prSlider.setValue(1);
        labelCaratteri.setText("# Characters: 200");
        idNews = -1;
    }

private void caricaTabella() {
        ArrayList<BeanNews> news = null;
        try {
            news = gestioneNews.ottieniAllNews();
        } catch (RemoteException e) {
            JLabel error = new JLabel("<html> <h2> Unable to communicate with the server eTour. </h2>" + "The list of <h3> <u> News is not loaded. </u> </h3>" + "<p> Please try again later. </p>" + "<p> If the error persists, please contact technical support. </p>" + "<p> We apologize for the inconvenience. </html>");
            Icon err = new ImageIcon(getClass().getResource(Home.URL_IMAGES + "error48.png"));
            JOptionPane.showInternalMessageDialog(this, error, "Error!", JOptionPane.ERROR_MESSAGE, err);
        } finally {
            tableModel = new NewsTableModel(news);
            tableNews.setModel(tableModel);
            // Text of news
            tableNews.getColumnModel().getColumn(0).setPreferredWidth(320);
            tableNews.getColumnModel().getColumn(0).setCellRenderer(new TestoNewsRenderer());
            // Priority
            tableNews.getColumnModel().getColumn(1).setPreferredWidth(100);
            tableNews.getColumnModel().getColumn(1).setCellRenderer(new PrioritaRenderer());
            // Date of entry
            tableNews.getColumnModel().getColumn(2).setPreferredWidth(80);
            // End Date
            tableNews.getColumnModel().getColumn(3).setPreferredWidth(80);
        }
    }
