public static String encode(String input) {
		if (input == null)
			return input;
		String str = input.replaceAll("<", "&lt;");
		str = str.replaceAll(">", "&gt;");
		str = str.replaceAll("\n", "<br />");
		return str;
	}

public static boolean URLOnSite(String input) {
		return !(input.contains("http://"));
	}
